export class Classic{getForm(){return`\n            <div id="dyjsform_container"></div>\n            <div  id="dyjsform_footer" class=" row form-group align-items-center">\n                <div class="col-md-3"></div>\n                <div class="col-md-6">\n                    <button type="button" class="form-control btn btn-primary dyjsform_action_add">\n                        <i class="fas fa-plus"></i> Ajouter\n                    </button>\n                </div>\n            </div>\n                <textarea rows="30" cols="150" name="dyjsform[dyjsform_options]"\n                       id="dyjsform_options"></textarea>\n            `}getField(field,rowIndex,BSColumnWidth){const type=field.type?`type="${field.type}"`:"";const value=field.value?`value="${field.value}"`:"";let content="";const className=field.className?`${field.className}`:"";const name=!field.name.startsWith("dyjsform_action_")?`name="dyjsform[${field.name}_${rowIndex}]"`:"";if(field.htmlElement==="select"&&field.options){content+=`<option></option>`;field.options.forEach((option=>{let selected=field.value===option.value?"selected":"";content+=`<option ${selected} value="${option.value}">${option.name}</option>`}))}else{content=field.content?`${field.content}`:""}return`<div class="form-group col-md-${BSColumnWidth}">\n            <div class="col-md-12">${field.label===""?"&nbsp;":field.label}</div>\n            <div class="col-md-12">\n                <${field.htmlElement} ${name} class="form-control ${field.name} ${className}" ${type} ${value} \n                data-row="${rowIndex}" data-name="${field.name}">${content}</${field.htmlElement}>\n                <span class="text-danger djf_error">${field.error}</span>\n            </div>\n        </div>`}}