class Dyjsform{constructor(entity=[]){this.entity=entity}getEntity(){return this.entity}setEntity(array){this.entity=array;return this}generateJson(){var data=[];document.querySelectorAll("#dysform .dysform_entity").forEach((function(ligne){var typeAdhesion=entity.querySelector(".dysform_type_adhesion").value;var codeAdhesion=entity.querySelector(".dysform_code_adhesion").value;var value=entity.querySelector(".dysform_value").value;data.push({type_adhesion:typeAdhesion,code_adhesion:codeAdhesion,value:value})}));document.querySelector("#dysform_options").value=JSON.stringify(data,null)}createEntity(typeAdhesion="",codeAdhesion="",value=""){var newLigne=`\n        <div class="row form-group align-items-center dysform_entity">\n            <div class="form-group col-md-3">\n                <div class="col-md-12">Libell√©</div>\n                <div class="col-md-12">\n                    <input class="form-control dysform_type_adhesion" type="text" value="${typeAdhesion}">\n                </div>\n            </div>\n            <div class="form-group col-md-3">\n                <div class="col-md-12">Code</div>\n                <div class="col-md-12">\n                    <input class="form-control dysform_code_adhesion" type="text" value="${codeAdhesion}">\n                </div>\n            </div>\n            <div class="form-group col-md-3">\n                <div class="col-md-12">Montant</div>\n                <div class="col-md-12">\n                    <input class="form-control dysform_value" type="text" value="${value}">\n                </div>\n            </div>\n            <div class="form-group col-md-3">\n                <div class="col-md-12">&nbsp;</div>\n                <div class="col-md-12">\n                    <button type="button" class="remove_entity btn btn-danger form-control">Supprimer</button>\n                </div>\n            </div>\n        </div>\n    `;document.querySelector("#dysform").insertAdjacentHTML("beforeend",newLigne)}loadJson(){var jsonString=document.querySelector("#dysform_options").value;if(jsonString){try{var jsonData=JSON.parse(jsonString);jsonData.forEach((function(item){this.createEntity(item.type_adhesion,item.code_adhesion,item.value)}))}catch(error){console.error("Erreur lors de l'analyse du JSON : ",error)}}}}