function generateJson(){var data=[];document.querySelectorAll("#mod_topweb_adhesion_form .mod_topweb_adhesion_ligne").forEach((function(ligne){var typeAdhesion=ligne.querySelector(".mod_topweb_adhesion_type_adhesion").value;var codeAdhesion=ligne.querySelector(".mod_topweb_adhesion_code_adhesion").value;var value=ligne.querySelector(".mod_topweb_adhesion_value").value;data.push({type_adhesion:typeAdhesion,code_adhesion:codeAdhesion,value:value})}));document.querySelector("#mod_topweb_adhesion_adhesion_choices").value=JSON.stringify(data,null)}function createLine(typeAdhesion="",codeAdhesion="",value=""){var newLigne=`\n        <div class="row form-group align-items-center mod_topweb_adhesion_ligne">\n            <div class="form-group col-md-3">\n                <div class="col-md-12">Libell√©</div>\n                <div class="col-md-12">\n                    <input class="form-control mod_topweb_adhesion_type_adhesion" type="text" value="${typeAdhesion}">\n                </div>\n            </div>\n            <div class="form-group col-md-3">\n                <div class="col-md-12">Code</div>\n                <div class="col-md-12">\n                    <input class="form-control mod_topweb_adhesion_code_adhesion" type="text" value="${codeAdhesion}">\n                </div>\n            </div>\n            <div class="form-group col-md-3">\n                <div class="col-md-12">Montant</div>\n                <div class="col-md-12">\n                    <input class="form-control mod_topweb_adhesion_value" type="text" value="${value}">\n                </div>\n            </div>\n            <div class="form-group col-md-3">\n                <div class="col-md-12">&nbsp;</div>\n                <div class="col-md-12">\n                    <button type="button" class="remove_ligne btn btn-danger form-control">Supprimer</button>\n                </div>\n            </div>\n        </div>\n    `;document.querySelector("#mod_topweb_adhesion_form").insertAdjacentHTML("beforeend",newLigne)}function loadJson(){var jsonString=document.querySelector("#mod_topweb_adhesion_adhesion_choices").value;if(jsonString){try{var jsonData=JSON.parse(jsonString);jsonData.forEach((function(item){createLine(item.type_adhesion,item.code_adhesion,item.value)}))}catch(error){console.error("Erreur lors de l'analyse du JSON : ",error)}}}